package com.ei.treasure.adapterimport android.content.Intentimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.ei.treasure.databinding.ActivityListItemBindingclass ActivityAdapter(private val activityClassNameList: ArrayList<String>) :    RecyclerView.Adapter<ActivityAdapter.ViewHolder>() {    companion object {        private const val TAG: String = "ActivityAdapter"    }    inner class ViewHolder(        val mBinding: ActivityListItemBinding    ) : RecyclerView.ViewHolder(mBinding.root)    // 根据布局创建一个view对象,布局中的空间并没有指定其内容    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {        // TODO: @eipip1e0 起一章 RecyclerView / Layout 来讲 attachToParent 参数        // ViewHolder views must not be attached when created. Ensure that you are not passing 'true' to the attachToRoot        return ViewHolder(            ActivityListItemBinding.inflate(                LayoutInflater.from(parent.context),                parent,                false            )        )    }    override fun onBindViewHolder(holder: ViewHolder, position: Int) {        val className: String = activityClassNameList[position]        holder.mBinding.button.apply {            text = className            setOnClickListener {                val activityClazz: Class<*> = Class.forName(className)                val intent = Intent(it.context, activityClazz)                it.context.startActivity(intent)            }        }    }    override fun getItemCount(): Int {        return activityClassNameList.size    }}