package com.ei.treasure.utilsimport android.content.Contextimport android.content.pm.ActivityInfoimport android.content.pm.PackageManagerclass GetActivityClazzListUtil {    companion object {        fun getActivityClazzList(context: Context): ArrayList<String> {            val activityClazzList = ArrayList<String>()            val packageManager: PackageManager = context.packageManager            val packageInfo = packageManager.getPackageInfo(                context.packageName,                PackageManager.GET_ACTIVITIES            )            val activities: Array<ActivityInfo> = packageInfo.activities            activities.forEach {                activityClazzList.add(it.name)            }            return activityClazzList        }    }}